// 容器组件  充当一个桥梁的作用，在UI组件和redux之间进行传输
// 引入UI组件
import CountUI from '../../components/count/CountReactRedux.jsx'

// 引入connect用于连接UI组件与redux
import { connect } from 'react-redux'

// 传递给UI组件的函数  返回值是对象  key作为传递给UI组件props的key  value就作为props的value
function a() {
  return { num: 900 }
}

// 使用connect()()创建并暴露一个容器组件
export default connect(a)(CountUI)
